import { Injectable } from '@angular/core';
import { IProduct } from './catalog/product.model';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CartService
{
  // This is the cart. It's an array of IProduct objects.
  cart: IProduct[] = [];

  
  constructor(private http:HttpClient) { }
  
  add(product: IProduct) {
    this.cart.push(product);
    //console.log('product ' + product.name + ' added to cart');
    //      console.log(`product ${product.name} added to cart`);
    // now post the cart to the server

    //  this.http.post('api/RobotCatalog', this.cart).subscribe(() =>
    //  {
    //    console.log('for product: ' + product.name + ' the response from server is: ' + '');
    //  })

    //this.http.post('/api/Cart', this.cart).subscribe((response) => {
    //  console.log('for product: ' + product.name + ' the response from server is: ' + response);
    //})

    // post this to the same server that the catalog is on,
    // but to a different endpoint: /api/Cart
    // This is accessed throght he cart service


    // DJS: just push the individual product to the server, not the whole cart
    
    this.http.post('/api/Cart', this.cart).subscribe((response) => {
      console.log('Added to cart: "' + product.name + '" with return code:' + response);
    })
  }
}
